import React, { Component } from 'react';
//import { CanvasBoard } from './CanvasBoard'
import './css/Pipline.css'

export class Pipline extends Component {
    static dislpayName = Pipline.name;

    render(){
        return (
            <section className="piplineSections">
                <div className="container">
                   <div className="row">
                        <div className="col-xxl-8 col-xl-8 col-lg-8">
                            <div className="canvasContainer">
                                <canvas id="piplineCanvas" onMouseDown={(e) => {
                                    let newEvent = e.nativeEvent;
                                    HandleMouseDown(newEvent)
                                }}>

                                </canvas>
                            </div>
                        </div>
                        <div className="col-xxl-4 col-xl-4 col-lg-4">
                            <div className="boardContainer">
                                <div className="canvasBoard_componen">
                                    <button className="btn btn-primary" onClick={activeCanvas}> Добавить новый трубопровод </button>
                                    <button className="btn btn-success"> Добавить узел </button>
                                    <button className="btn btn-danger"> Удалить узел </button>
                                    <button className="btn btn-warning"> Рассчитать пересечения</button>
                                </div>
                            </div>
                            <div className="piplineElements">
                                sdsa
                            </div>
                        </div>
                   </div>
                </div>    
            </section>
           
        );
    }


    
}

let piplines = []
let mouseDown = false



function HandleMouseDown(event){
    mouseDown = true
    console.log(event)
}


let log = () => {
    console.log('kek')
}

let activeCanvas = () => {
    let canvas = document.getElementById('piplineCanvas').classList.toggle('canvas_active')
}

function DrawPipeline() {
    var canvas = document.getElementById('canvas');
    var ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    if (points.length > 0) {
        DrawLines();
    }
    for (var i = 0; i < points.length; i++) {
        if (i === selectedNumber)
            DrawPoint(points[i].x, points[i].y, "red");
        else
            DrawPoint(points[i].x, points[i].y, "black");
    }

}

function DrawPoint(x, y, color) {
    var canvas = document.getElementById('canvas');
    var ctx = canvas.getContext('2d');
    ctx.beginPath();
    ctx.arc(x, y, 5, 0, getRadians(360));
    ctx.fillStyle = color;
    ctx.moveTo(x, y);
    ctx.fill();
}

function DrawLines() {
    var canvas = document.getElementById('canvas');
    var ctx = canvas.getContext('2d');
    ctx.beginPath();
    ctx.moveTo(points[0].x, points[0].y);
    for (var i = 1; i < points.length; i++) {
        ctx.lineTo(points[i].x, points[i].y);
    }
    ctx.stroke();
}
