import React, { Component } from 'react';
import { render } from 'react-dom'
//import { CanvasBoard } from './CanvasBoard'
//mport { PiplineSegment } from './PiplineSegment'
import './css/Pipline.css'
import { PiplineSegment } from './PiplineSegment';

export class Pipline extends Component {
    static dislpayName = Pipline.name;

    state = {
        piplinesSegmentCount: 0
    }

    render(){
        return (
            <section className="piplineSections">
                <div className="container">
                   <div className="row">
                        <div className="col-xxl-8 col-xl-8 col-lg-8">
                            <div className="canvasContainer">
                                <canvas id="piplineCanvas" width="740" height="425"
                                    onMouseDown={(e) => {
                                        let newEvent = e.nativeEvent;
                                        HandleMouseDown(newEvent)
                                    }}
                                    onMouseUp={e => {
                                        let nativeEvent = e.nativeEvent;
                                        HandleMouseUp(nativeEvent);
                                    }}
                                    onMouseMove={e => {
                                        let nativeEvent = e.nativeEvent;
                                        HandleMouseMove(nativeEvent);
                                    }}                                >
                                </canvas>
                            </div>
                        </div>
                        <div className="col-xxl-4 col-xl-4 col-lg-4">
                            <div className="boardContainer">
                                <div className="canvasBoard_componen">
                                    <button className="btn btn-primary" onClick={activeCanvas, AddPipline}> Добавить новый трубопровод </button>
                                    <button className="btn btn-success" onClick={SetAddPointActive}> Добавить узел </button>
                                    <button className="btn btn-danger" > Удалить узел </button>
                                    <button className="btn btn-warning"> Рассчитать пересечения</button>
                                </div>
                            </div>
                            <div className="piplineElements" id="piplineItems">
                                <ul className="piplineList" id="piplineList">
                                    
                                </ul>
                            </div>
                        </div>
                   </div>
                </div>    
            </section>
           
        );
    }


    
}



let piplines = []
let mouseDown = false
let piplinesCount = 0;
let defaultId = 0;
let defaultPoint = [0, 0]
let AddPointActive = false
let downOnSelected = false;
let points = []
let selectedNumber = -1;
let activePipline = -1;

 

function SetAddPointActive(pipline, point) {
    AddPointActive = true;
    console.log("Add active");
}

let log = () => {
    console.log('kek')
}

function renderPiplineItems() {
    return render(
        piplines.map((item) => (
            <li key={item.id} id={item.id + "pipline"}>
                <button className="pipline-btn" type="button" onClick={() =>setPipline(item.id)}>{item.name}</button> 
                    <button type="button" className="close red" aria-label="Close" onClick={()=>DeletePipline(item.id)}>
                            <span aria-hidden="true">&times;</span>
                    </button>
            </li>
        )),
        document.getElementById('piplineList')
    );
}

function setPipline(id) {
   
    if (activePipline !== id && activePipline !== -1) {
        document.getElementById(activePipline + "pipline").classList.remove('pipline-btn__active')
    }
    let pipline = document.getElementById(id + "pipline")
    pipline.classList.add('pipline-btn__active')
    activePipline = id;
}

function getRandomColor() {
    var letters = '0123456789ABCDEF';
    var color = '#';
    for (var i = 0; i < 6; i++) {
        color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
}

function AddPipline() {
    let color = getRandomColor()
    let pipline = {
        id: piplinesCount + 1,
        name: "Трубопровод " + (piplinesCount + 1),
        color: color,
        points: []
    }
   
    piplines.push(pipline)
    piplinesCount += 1
    renderPiplineItems()
    if (piplines.length <= 1) {
        activePipline = pipline.id
        setPipline(pipline.id)
    }

}


//Проверить перед деплоем
function DeletePipline(id) {

    for (const key in piplines) {
        if (Object.hasOwnProperty.call(piplines, key)) {
            const pipline = piplines[key];
            if(pipline.id === id){
                console.log(piplines);
                piplines.splice(key, key)
                console.log(piplines);
                renderPiplineItems();
                DrawPipeline();
            }
        }
    }
}

let activeCanvas = () => {
    let canvas = document.getElementById('piplineCanvas').classList.toggle('canvas_active')
}


let addPointsToPipline = (piplineIndex, x, y) =>{
    let point = {x, y};
    piplines[piplineIndex-1].points.push(point);
}

function TrySelectPoint(x, y) {
    for (var i = 0; i < points.length; i++) {
        var point = points[i];
        if (x >= point.x - 5 && x <= point.x + 5 && y >= point.y - 5 && y <= point.y + 5) {
            selectedNumber = i;
            DrawPipeline();
            return true;
        }
    }
    console.log(points)
    return false;
}
function HandleMouseDown(event) {
    mouseDown = true;
    if (AddPointActive) {
        var x = event.offsetX;
        var y = event.offsetY;
        addPointsToPipline(activePipline, x, y)
        DrawPipeline();
        AddPointActive = false;
    }
    else {
        x = event.offsetX;
        y = event.offsetY;
        downOnSelected = TrySelectPoint(x, y);
    }

}
function HandleMouseMove(event) {
    if (mouseDown) {
        if (downOnSelected) {
            var x = event.offsetX;
            var y = event.offsetY;
            var point = points[selectedNumber];
            point.x = x;
            point.y = y;
            DrawPipeline();
        }
    }
}

function HandleMouseUp() {
    mouseDown = false;
    downOnSelected = false;
}

function DrawPipeline() {
    var canvas = document.getElementById('piplineCanvas');
    var ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    if (points.length > 0) {
        console.log('driwe line');
        DrawLines();
    }
    piplines.forEach(pipline => {
        for (const key in pipline.points) {
            if (Object.hasOwnProperty.call(pipline.points, key)) {
                if(key.points.length > 0){
                    DrawLines();
                }
                const point = pipline.points[key];
                console.log('draw point');
                DrawPoint(point.x, point.y, pipline.color)
            }
        }
    });

}

function DrawPoint(x, y, color) {
    var canvas = document.getElementById('piplineCanvas');
    var ctx = canvas.getContext('2d');
    ctx.beginPath();
    ctx.arc(x, y, 5, 0, getRadians(360));
    ctx.fillStyle = color;
    ctx.moveTo(x, y);
    ctx.fill();
}

function DrawLines() {
    var canvas = document.getElementById('piplineCanvas');
    var ctx = canvas.getContext('2d');
    console.log('drawLines');
    for (const key in piplines) {
        if (Object.hasOwnProperty.call(piplines, key) ) {
            const pipline = piplines[key];
            if(pipline.points.length > 0 ){
                ctx.moveTo(pipline.points[0].x, pipline.points[0].y);
                console.log(pipline);
                for (var i = 0; i < pipline.points.length; i++) {
                    ctx.lineTo(pipline.points[i].x, pipline.points[i].y);
                    ctx.strokeStyle = pipline.color;
                    
                }
            }
        }
        ctx.stroke()
        ctx.beginPath();
    }

}

function getRadians(degrees) {
    return (Math.PI / 180) * degrees;
}
